                if (!empty($IDServicioElemento)) {
                    $condicion_multiple_elemento = SIMWebService::verifica_elemento_otro_servicio($IDServicioElemento, $IDServicio);
                    $array_multiple_id = explode(",", $condicion_multiple_elemento);
