                            $respuesta = "Lo sentimos servicio cerrado el dia: " . $Fecha . " motivo: " . $r_cierre_servicio["Descripcion"];
                        }
                    } else {
                        $respuesta = "Lo sentimos servicio cerrado el dia: " . $Fecha . " Motivo: " . $r_cierre_servicio["Descripcion"];
                    }
                }

            }

            return $respuesta;
