                    $reserva["NombreServicio"] = $dbo->getFields("ServicioMaestro", "Nombre", "IDServicioMaestro = '" . $id_servicio_maestro . "'");
                    $reserva["IDElemento"] = $row_reserva["IDServicioElemento"];
