                                                    //$auxiliar[ "Tipo" ] = $dbo->getFields( "AuxiliarTipo", "Nombre", "IDAuxiliarTipo = '" . $tipo_auxiliar . "'" );
                                                    $array_id_aux[] = $IDAuxiliar;
                                                    array_push($response_auxiliar, $auxiliar);
