                                            endif;
                                        endif;
                                    endif;
                                }
                            endforeach;
                        endif;
                    }

                    if (count($response_auxiliar) > 0):

                        foreach ($response_auxiliar as $key_aux => $value_aux) {
                            if ((int) $value_aux["Orden"] == 0) {
