                                                        endif;

                                                        //SIMUtil::notificar_nuevo_invitado( $IDClub, $IDSocio, $NumeroDocumento, $nombre_enviar , $FechaIngreso );

                                                        //Guardo los datos de los campos
                                                        $datos_formulario = json_decode($ValoresFormulario, true);
                                                        if (count($datos_formulario) > 0):
