                                    $sql_inserta_reserva = $dbo->query("INSERT INTO ReservaGeneral (IDClub, IDSocio, IDServicio, IDServicioElemento, IDEstadoReserva, IDDisponibilidad, Fecha, Hora, Observaciones, Tee, UsuarioTrCr, FechaTrCr)
