                            $respuesta["success"] = false;
                            $respuesta["response"] = null;
                        endif;
                    else:
                        $respuesta["message"] = "Lo sentimos no se puede reservar turnos seguidos ";
                        $respuesta["success"] = false;
                        $respuesta["response"] = null;

                    endif;
                } else {
                    $respuesta["message"] = "Error el socio no existe o no pertenece al club";
