    public function set_cambiar_clave($IDClub, $IDSocio, $Clave = "", $SegundaClave = "", $Correo = "")
    {
        $dbo = &SIMDB::get();
        if (!empty($IDClub) && !empty($IDSocio) && !empty($Clave)) {

