            $id_socio = $dbo->getFields("Socio", "IDSocio", "IDSocio = '" . $IDSocio . "' and IDClub = '" . $IDClub . "'");

            if (!empty($id_socio)) {

                if (!empty($Correo)) {
                    $sql_cambiar_correo = "Update Socio Set CorreoElectronico= '" . $Correo . "' Where IDSocio = '" . $IDSocio . "' and IDClub = '" . $IDClub . "'";
                    $dbo->query($sql_cambiar_correo);
                }

                if (!empty($Clave)):
