	function get_hora_disponible( $IDClub, $IDServicio, $Fecha, $Hora, $Admin = "" ) {
		$dbo = & SIMDB::get();

		$fecha_disponible = 0;
		$verifica_disponibilidad_especifica = 0;
		$verifica_disponibilidad_general = 0;

