			$where .= " AND ( Accion = '" . $IDSocio . "' OR Nombre LIKE '%" . $IDSocio . "%' OR Apellido LIKE '%" . $IDSocio . "%'  )   ";
			if ( empty( $Fecha ) )
				$where .= " AND Fecha >= CURDATE()  ";

			$order = " ReservaGeneral.Fecha ASC, ReservaGeneral.Hora ASC ";
		}


