			$permite_repetir = "S";
		else :
			$permite_repetir = "N";
		endif;
		if ( $permite_repetir == "S" ):
			$mensaje_especial_repetir = " Se realizara una reserva automatica para el dÃ­a " . date( "Y-m-d", $RepetirFechaFinal );
